INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (13, 146, 1, 72, 'I had Izzy for health class in highschool. When she came over to the house shed have a beer with dad and made meswear never to tell anyone at school that she drank. She was this tiny littleold lady and was the most of the most respected and feared teachers.', 'Anita', NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (14, 237, 1, NULL, '[Nicholas Keem] said his father Martin Keem Sr. while blasting in the Erie Canal had the misfortune of fracturing a leg, whereupon he came to Sheldon for asylum and care.  This would indicate kinfolk.  Furthermore, the incident happened before 1826, when the canal was in operation.', NULL, NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (15, 62, 1, 72, 'I always thought Uncle Bob and Aunt Catherine were such a good looking couple.  They were both a lot of fun and livened things up when we were all together.', 'Anita', NULL, true, '2005-08-29', now());
