INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (17, 23, 1, NULL, 'The family moved up to Burlington when Stanley was in high school.  Bessie & Louis moved back down to MA when Norman was a senior in med school, so Norman and Stanley lived together for a couple years, renting rooms with a professor.  One year Stanley had the lead in the high school play, and Norman practiced with him, training him on expressions.', NULL, NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (18, 15, 1, 24, 'She would make a penny go a mile.  [paraphrased: She was very clean, and swept the steps and the sidewalk.  She was strong in the household.]', 'Norman', NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (19, 24, 1, NULL, 'In high school, Norman was the president of the \"Quarter Club\"- they had 25 members and it cost 25 cents to come and play pool.  When Norman moved up to Burlington, he was given the pool table, and they kept it in the attic.', NULL, NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (20, 25, 2, NULL, 'In Burlington, Norman worked in Matthews/Louis shoe store on Friday afternoons.  Annette was the cashier at the store.  Every Friday night theyd go dancing at a local roller rink that would bring in an outside band.  On Sunday theyd go horseback riding.  Also Norman got a \"fold boat\" and he bought a lanteen sail and a keel for it- theyd go out together on Lake Champlain.', NULL, NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (21, 24, 1, NULL, 'Norman was a medical officer for a platoon.  The family lived together in Texas for 2 years.  Beth was born during this time, and they rented a home rather than living in barracks.  Norman was scheduled to go over to Japan, but the war ended before it was time.', NULL, NULL, true, '2005-08-29', now());
INSERT INTO Notes (id, ref_id, type, author, body, author_name, date, active, created_at, updated_at) VALUES (22, 30, 1, 24, 'Larry was very kind- he was good to the whole family.', 'Norman', NULL, true, '2005-08-29', now());
